require(['jquery', 'xwiki-events-bridge'], function ($) {
  /*
   * Inject a button for user import in the Users and Groups administration sections.
   */
  var importUsersButton = $('<button/>').attr({
    'id': 'importUserButton',
    'class': 'btn btn-primary',
    'type': 'button',
    'data-toggle': 'modal',
    'data-target': '#importUsersModal'
  });
  importUsersButton.text("$services.localization.render('importUsers.title')");
  /*
   * Append the Import User button in several needed places.
   * 1. In any Group page, under the livetable.
   * 2. In any Group's pop-up edit modal, next to the Add(member) button.
   * 3. In the Groups administration section, next to Create Group button.
   * 4. In the Users administration section, next to Create User button, in the main wiki.
   * 5. In the Users administration section, next to Add and Invite buttons, in any subwiki.
   */
  $.each([
    $("button[data-target^='#create'][data-target$='Modal']"),
    $('#groupusers'),
    $('#inviteUserButton')],
    function() {
      $(this).parent().append(importUsersButton);
    }
  );
  // The only case not covered in the above code is 2. In any Group's pop-up edit modal, next to the Add(member) button.
  $(document).on('xwiki:dom:updated', function() {
    // Instead of displaying the Add User form on full modal width, split the width in 2 columns to also add the Import
    // User button.
    var editForm = $('#editGroupModal form');
    editForm.wrap("<div class='row'></div>").addClass('col-sm-6');
    var importSection = $('<div/>').attr({'class': 'col-sm-6'});
    var importInfo = $('<p/>').attr({'class': 'importInfo'});
    importInfo.text("$services.localization.render('importUsers.info')");
    importSection.append(importInfo);
    // The same element cannot be appended in different places.
    var importUsersButtonClone = importUsersButton.clone();
    importSection.append(importUsersButtonClone);
    editForm.parent().append(importSection);
  });
  // Hide the Edit Group modal, when using the Import Users feature.
  $('#editGroupModal').on('click', '#importUserButton', function() {
    $('#editGroupModal').modal('hide');
  });
});

require(['jquery'], function ($) {
  // Set the groupReference value when accessing the edit group modal.
  $('#groupstable').on('click', '.actionedit', function() {
    var groupReference = $(this).closest('tr').find('.group').data('reference');
    $("#importUsersModal input[name='groupReference']").val(groupReference);
  });
  var importUsersServiceDoc = new XWiki.Document('LDAPUserImportService', 'LDAPUserImport');
  var resultsContainer = $('#searchResults');
  // Get user results from LDAP.
  $('#triggerSearch').click(function() {
    $('#ldapSearchBox').removeClass('hidden');
    resultsContainer.html($('#searchResultsLoading').html())
    var allFields = $('#importUserModal-allFields').val();
    var singleField = $('#importUserModal-userField').val();
    var searchInput = $('#importUserModal-userFieldValue').val();
    var formToken = $('input[name=form_token]').val();
    var params = {
      'allFields': allFields,
      'singleField': singleField,
      'searchInput': searchInput,
      'form_token': formToken,
      'outputSyntax': 'plain'
    };
    var url = importUsersServiceDoc.getURL('get');
    $.post(url, $.param(params)).done(function(users) {
      if (users == null) {
        resultsContainer.html("$services.localization.render('importUsers.modal.fieldValue.noResults')");
      } else {
        var resultsList = $('<ul/>').attr({'id': 'usersList'});
        $.each(users, function(index, value) {
          var displayedText = value['givenName'] + ' ' + value['cn'] + ' (' + value['mail'] + ')';
          var mention = 'notImported';
          var mentionText = "$services.localization.render('importUsers.modal.user.notImported')";
          var listItem = $('<li/>');
          // If the user exists, it will be represented as plain text.
          // If the user doesn't exist, it will be represented as a label and a checkbox to be selected for import.
          if (value.exists === 'true') {
            listItem.text(displayedText);
            mention = 'imported';
            mentionText = "$services.localization.render('importUsers.modal.user.imported')";
          } else {
            var checkbox = $('<input/>').attr({
              'type': 'checkbox',
              'name': 'user',
              'id': index,
              'value': index
            });
            var label = $('<label/>').attr({
              'for': index
            }).text(displayedText);
            listItem.append(checkbox);
            listItem.append(label);
          }
          listItem.attr({'class': mention});
          var mentionSpan = $('<span/>').text(mentionText);
          listItem.append(mentionSpan);
          resultsList.append(listItem);
        });
        resultsContainer.html(resultsList);
      }
    });
  });
  // Perform the user import.
  $('#importUsersModal form').submit(function(event) {
    event.preventDefault();
    var formData = $(this).serialize();
    var url = importUsersServiceDoc.getURL('get', 'outputSyntax=plain&action=import');
    $.post(url, $(this).serialize()).done(function(users) {
      if (users == null) {
        resultsContainer.html("$services.localization.render('importUsers.modal.user.failedImport')");
      } else {
        var resultsList = $('<ul/>').attr({'id': 'importedUsersList'});
        $.each(users, function(index, value) {
          var listItem = $('<li/>');
          var userLink = $('<a/>').attr({
            'href': value
          }).text(index);
          listItem.append(userLink);
          resultsList.append(listItem);
          resultsContainer.html(resultsList);
        });
      }
    });
  });
});

